document.addEventListener("DOMContentLoaded",(function(){var e=document.querySelectorAll(".chatdope-contact");e.forEach((function(t){t.addEventListener("click",(function(){e.forEach((function(e){return e.classList.remove("active")})),t.classList.add("active");var n=t.dataset.contactId;fetch("your-ajax-endpoint-url?contact_id=".concat(n)).then((function(e){return e.ok?e.text():Promise.reject("Network response was not ok.")})).then((function(e){document.getElementById("chatdope-chats").innerHTML=e})).catch((function(e){console.error("Error fetching chat messages:",e)}))}))})),document.getElementById("chatdope-send").addEventListener("click",(function(){var e=document.getElementById("chatdope-input").value;fetch("your-ajax-send-message-endpoint-url",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({message:e})}).then((function(e){if(!e.ok)throw new Error("Network response was not ok.")})).catch((function(e){console.error("Error sending message:",e)}))}))}));